import { useState, useEffect } from 'react'
import axios from "axios";
import { Link } from "react-router-dom";
import { saveAs } from 'file-saver'

const EmployeeList = () => {
    const [employees, setEmployee] = useState([]);
 
    useEffect(() => {
        getEmployees();
    }, []);
 
    const getEmployees = async () => {
        const response = await axios.get('http://localhost:5000/employees');
        setEmployee(response.data);
    }
 
    const deleteEmployee = async (id) => {
        await axios.delete(`http://localhost:5000/employees/${id}`);
        getEmployees();
    }

    async function printTickets() {
        const { data } = await getTicketsPdf()
        const blob = new Blob([data], { type: 'application/pdf' })
        saveAs(blob, "CV.pdf")
    }
      
    async function getTicketsPdf() {
        return axios.get(`http://localhost:5000/getPdf`, {
            headers: {
            'Content-Type': 'multipart/form-data'
            },
            responseType: 'arraybuffer'
        })
    }

    return (
        <div>
            <Link to="/add" className="button is-primary mt-2">Add New</Link>
            <table className="table is-striped is-fullwidth">
                <thead>
                    <tr>
                        <th>No</th>
                        <th>Name</th>
                        <th>Surname</th>
                        <th>Email</th>
                        <th>Phone</th>
                        <th>Born</th>
                        <th>CV</th>
                    </tr>
                </thead>
                <tbody>
                    { employees.map((employee, index) => (
                        <tr key={ employee.id }>
                            <td>{ index + 1 }</td>
                            <td>{ employee.Name }</td>
                            <td>{ employee.Surname }</td>
                            <td>{ employee.Email }</td>
                            <td>{ employee.Phone }</td>
                            <td>{ employee.Born }</td>
                            <td><button onClick={()=>printTickets()}>Open</button></td>
                            <td>
                                <Link to={`/edit/${employee.id}`} className="button is-small is-info">Edit</Link>
                                <button onClick={ () => deleteEmployee(employee.id) } className="button is-small is-danger">Delete</button>
                            </td>
                        </tr>
                    )) }
                     
                </tbody>
            </table>
        </div>
    )
}

export default EmployeeList
