import { useState } from 'react'
import axios from "axios";
import { useNavigate } from 'react-router-dom';
 
const AddEmployee = () => {
    const [Name, setName] = useState('');
    const [Surname, setSurname] = useState('');
    const [Email, setEmail] = useState('');
    const [Born, setBorn] = useState('');
    const [Phone, setPhone] = useState('');
    const [file, setFile] = useState('');
    const history = useNavigate();
 
    const saveEmployee = async (e) => {
        e.preventDefault();
        var sampleFile= ".."+"/upload/"+ document.getElementById("fileName").files[0].name;
        console.log(sampleFile);
        await axios.post('http://localhost:5000/employees',{
            Name: Name,
            Surname: Surname,
            Email: Email,
            Born: Born,
            Phone: Phone
        });

        
        //history("/");
    }

    const _handleUpload = (e) => {
        const dataForm=new FormData();
        dataForm.append('file',e.target.files[0]);
        axios.post('http://localhost:5000/upload',dataForm)
        .then(res=> {

        })
        .catch(err=>console.log(err));
    }
 
    return (
        <div>
            <form onSubmit={ saveEmployee }>
                <div className="field">
                    <label className="label">Name</label>
                    <input 
                        className="input"
                        type="text"
                        placeholder="Name"
                        value={ Name }
                        onChange={ (e) => setName(e.target.value) }
                        required/>
                </div>
 
                <div className="field">
                    <label className="label">Surname</label>
                    <input 
                        className="input"
                        type="text"
                        placeholder="Surname"
                        value={ Surname }
                        onChange={ (e) => setSurname(e.target.value) }
                        required/>
                </div>

                <div className="field">
                    <label className="label">Email</label>
                    <input 
                        className="input"
                        type="email"
                        placeholder="Email"
                        value={ Email }
                        onChange={ (e) => setEmail(e.target.value) }
                        required/>
                </div>

                <div className="field">
                    <label className="label">Born</label>
                    <input 
                        className="input"
                        type="date"
                        placeholder="Born"
                        value={ Born }
                        onChange={ (e) => setBorn(e.target.value) }
                        required/>
                </div>

                <div className="field">
                    <label className="label">Phone</label>
                    <input 
                        className="input"
                        type="text"
                        placeholder="+370"
                        pattern="[+370][0-9]{11}"
                        title="Pvz.: +37066666666"
                        value={ Phone }
                        onChange={ (e) => setPhone(e.target.value) }
                        required/>
                </div>
                
                <div className="field">
                    <label className="label">CV:</label>
                    <input
                        id="fileName"
                        className="input"
                        type="file"
                        value={file}
                        onChange={ (e) => setFile(e.target.value)}
                    />
                </div>
                <div className="field">
                    <button className="button is-primary">Save</button>
                </div>
            </form>
        </div>
    )
}



export default AddEmployee
