/* eslint-disable react-hooks/exhaustive-deps */
import { useState, useEffect } from 'react'
import axios from "axios";
import { useNavigate, useParams } from 'react-router-dom';
 
const EditEmployee = () => {
    const [Name, setName] = useState('');
    const [Surname, setSurname] = useState('');
    const [Email, setEmail] = useState('');
    const [Born, setBorn] = useState('');
    const [Phone, setPhone] = useState('');
    const history = useNavigate();
    const { id } = useParams();
 
    const updateEmployee= async (e) => {
        e.preventDefault();
        await axios.patch(`http://localhost:5000/employees/${id}`,{
            Name: Name,
            Surname: Surname,
            Email: Email,
            Born: Born,
            Phone: Phone
        });
        history("/");
    }
 
    useEffect(() => {
        getEmployeeById();
    }, []);
 
    const getEmployeeById = async () => {
        const response = await axios.get(`http://localhost:5000/employees/${id}`);
        setName(response.data.Name);
        setSurname(response.data.Surname);
        setEmail(response.data.Email);
        setBorn(response.data.Born);
        setPhone(response.data.Phone);
    }
 
    return (
        <div>
            <form onSubmit={ updateEmployee }>
                <div className="field">
                    <label className="label">Name</label>
                    <input
                        className="input"
                        type="text"
                        placeholder="Name"
                        value={ Name }
                        onChange={ (e) => setName(e.target.value) }
                        required/>
                </div>
 
                <div className="field">
                    <label className="label">Surname</label>
                    <input 
                        className="input"
                        type="text"
                        placeholder="Surname"
                        value={ Surname }
                        onChange={ (e) => setSurname(e.target.value) }
                        required/>
                </div>

                <div className="field">
                    <label className="label">Email</label>
                    <input 
                        className="input"
                        type="email"
                        placeholder="Email"
                        value={ Email }
                        onChange={ (e) => setEmail(e.target.value) }
                        required/>
                </div>

                <div className="field">
                    <label className="label">Born</label>
                    <input 
                        className="input"
                        type="date"
                        placeholder="Born"
                        value={ Born }
                        onChange={ (e) => setBorn(e.target.value) }
                        required/>
                </div>

                <div className="field">
                    <label className="label">Phone</label>
                    <input 
                        className="input"
                        type="tel"
                        placeholder="+370"
                        value={ Phone }
                        onChange={ (e) => setPhone(e.target.value) }
                        required/>
                </div>
 
                <div className="field">
                    <button onClick={()=>Function() } className="button is-primary">Update</button>
                </div>
            </form>
        </div>
    )
    /*function Function()
    {
        const elements = document.getElementsByClassName("input");
        console.log(elements);
        //window.location.href="/";
    }*/
}
 


export default EditEmployee
