import { useState, useEffect } from 'react'
import axios from "axios";
import { Link } from "react-router-dom";
import { saveAs } from 'file-saver'
import TextField from '@mui/material/TextField';

const EmployeeList = () => {
    const [employees, setEmployee] = useState([]);
    const [searchTerm, setSearchTerm] = useState("");

    useEffect(() => {
        getEmployees();
    }, []);
 
    const getEmployees = async () => {
        const response = await axios.get('http://localhost:5000/employees');
        setEmployee(response.data);
    }
 
    const deleteEmployee = async (id) => {
        await axios.delete(`http://localhost:5000/employees/${id}`);
        getEmployees();
    }

    async function printTickets(email) {
        const { data } = await getTicketsPdf(email)
        const blob = new Blob([data], { type: 'application/pdf' })
        saveAs(blob, "CV.pdf")
    }
      
    async function getTicketsPdf(email) {
        return axios.get(`http://localhost:5000/getPdf`, {
            headers: {
            'Content-Type': 'multipart/form-data'
            },
            params: {
                Email: email
            },
            responseType: 'arraybuffer'
        })
    }

    return (
        <div>
            <Link to="/add" className="button is-primary mt-2">Add New</Link>
                <TextField
                    width="2ch"
                    id="outlined-basic"
                    variant="outlined"
                    label="Search"
                    size="small"
                    margin="dense"
                    onChange={event=>{setSearchTerm(event.target.value)}}
                />
            
            <table className="table is-striped is-fullwidth">
                <thead>
                    <tr>
                        <th>Nr.</th>
                        <th>Vardas</th>
                        <th>PavardÄ—</th>
                        <th>Email</th>
                        <th>Phone</th>
                        <th>Born</th>
                        <th>CV</th>
                        <th></th>
                    </tr>
                </thead>
                <tbody>
                    { employees.filter((val)=>{
                        if(searchTerm == "") return val;
                        else if(searchTerm.includes(" "))
                        {
                            const result = searchTerm.split(/\s+/);
                            if(result.length==2)
                            {
                                if(result[1]=="")
                                {
                                    if(val.Name.toLowerCase().includes(result[0].toLowerCase()))
                                        return val;
                                    else if(val.Surname.toLowerCase().includes(result[0].toLowerCase()))
                                        return val;
                                }
                                else
                                {
                                    if(val.Name.toLowerCase().includes(result[0].toLowerCase()) && val.Surname.toLowerCase().includes(result[1].toLowerCase()))
                                        return val;
                                    else if(val.Name.toLowerCase().includes(result[1].toLowerCase()) && val.Surname.toLowerCase().includes(result[0].toLowerCase()))
                                        return val;
                                }
                            }
                        }
                        else if(val.Name.toLowerCase().includes(searchTerm.toLowerCase()))
                        {
                            return val;
                        }
                        else if(val.Surname.toLowerCase().includes(searchTerm.toLowerCase()))
                        {
                            return val;
                        }
                    }).map((employee, index) => (
                        <tr key={ employee.id }>
                            <td>{ index + 1 }</td>
                            <td>{ employee.Name }</td>
                            <td>{ employee.Surname }</td>
                            <td>{ employee.Email }</td>
                            <td>{ employee.Phone }</td>
                            <td>{ employee.Born }</td>
                            <td><button onClick={()=>printTickets(employee.Email)}>Open</button></td>
                            <td>
                                <Link to={`/edit/${employee.id}`} className="button is-small is-info">Edit</Link>
                                <button onClick={ () => deleteEmployee(employee.id) } className="button is-small is-danger">Delete</button>
                            </td>
                        </tr>
                    )) }
                     
                </tbody>
            </table>
        </div>
    )
}

export default EmployeeList
