import { useState, useEffect } from 'react'
import axios from "axios";
import { Link } from "react-router-dom";

const EmployeeList = () => {
    const [employees, setEmployee] = useState([]);
 
    useEffect(() => {
        getEmployees();
    }, []);
 
    const getEmployees = async () => {
        const response = await axios.get('http://localhost:5000/employees');
        setEmployee(response.data);
    }
 
    const deleteEmployee = async (id) => {
        await axios.delete(`http://localhost:5000/employees/${id}`);
        getEmployees();
    }

    const printTickets = async() =>{
        let file = await getTicketsPdf();
        console.log(file);
        const blob = new Blob([file], {type: 'application/pdf'});
        const link = document.createElement('a');
        link.href = window.URL.createObjectURL(blob);
        link.download = 'tickets.pdf';
        document.body.appendChild(link);
        link.click();
  
        setTimeout(function() {
          document.body.removeChild(link);
      }, 100);
    }

    return (
        <div>
            <Link to="/add" className="button is-primary mt-2">Add New</Link>
            <table className="table is-striped is-fullwidth">
                <thead>
                    <tr>
                        <th>No</th>
                        <th>Name</th>
                        <th>Surname</th>
                        <th>Email</th>
                        <th>Phone</th>
                        <th>Born</th>
                        <th>CV</th>
                    </tr>
                </thead>
                <tbody>
                    { employees.map((employee, index) => (
                        <tr key={ employee.id }>
                            <td>{ index + 1 }</td>
                            <td>{ employee.Name }</td>
                            <td>{ employee.Surname }</td>
                            <td>{ employee.Email }</td>
                            <td>{ employee.Phone }</td>
                            <td>{ employee.Born }</td>
                            <td><button>Open</button></td>
                            <td>
                                <Link to={`/edit/${employee.id}`} className="button is-small is-info">Edit</Link>
                                <button onClick={ () => deleteEmployee(employee.id) } className="button is-small is-danger">Delete</button>
                            </td>
                        </tr>
                    )) }
                     
                </tbody>
            </table>
        </div>
    )
}

export default EmployeeList
